(function(){$(function(){var t=abp.services.app.question,n=$("#QuestionEditForm");n.find("input:first").focus();n.validate();n.find('button[type="submit"]').click(function(i){if(i.preventDefault(),n.valid()){var r=n.serializeFormToObject();t.editQuestion(r).done(function(n){location.href="/Questions/Details/"+n.question.id})}})})})();